const counter = new Store({
  value: 1,
  actions: {
    add(value, num) {
      this.next(value + num);
    }
  }
});

counter.subscribe((v) => console.log('counter is', v)); // 'counter is 1'
counter.add(3); // note no value parameter; 'counter is 4'
